function Search(){const searchForm=document.getElementById("search_form"),searchButton=document.getElementById("search_button"),searchInput=document.getElementById("search_input"),searchResults=document.getElementById("search_results"),searchModal=document.getElementById("search_modal");searchForm.addEventListener("submit",(e)=>{e.preventDefault();let link=document.createElement("a");link.href=`/users/${searchInput.value}`,link.click()}),searchButton.addEventListener("click",(e)=>{openDialog(e)}),document.addEventListener("keypress",function(e){const activeElement=document.activeElement;if(activeElement.tagName==="INPUT"){if(activeElement.id!=="search_input")return}else if(activeElement.tagName==="TEXTAREA")return;else if(activeElement.getAttribute("contenteditable")==="true")return;if(e.key==="/")openDialog(e)});function openDialog(e){if(searchModal.open)return;e.preventDefault(),searchResults.innerHTML="",searchModal.showModal(),searchInput.value="",setTimeout(()=>{searchInput.focus()},0)}}Search();
